    [CARS]: {
      screen: [CARS], // screen (actually a navigator)
      stackName: [PRIVATE], // parent stack
      stack: {
        [MY_CARS]: {
          screen: [MY_CARS], // a simple screen this time (leaf)
          stackName: [CARS],
        },
        [INCOMPLETE_DRIVES]: {
          screen: [INCOMPLETE_DRIVES],
          stackName: [CARS],
        },
        [ACTIVATE_CAR]: {
